--SELECT * FROM people JOIN stars ON people.id = stars.person_id JOIN movies ON movies.id = stars.movie_id WHERE person_id IN (SELECT id FROM people WHERE name = 'Kevin Bacon' AND birth = 1958);
SELECT name FROM people WHERE id IN (SELECT person_id FROM stars WHERE movie_id IN (SELECT movies.id FROM people JOIN stars ON people.id = stars.person_id JOIN movies ON movies.id = stars.movie_id WHERE person_id IN (SELECT id FROM people WHERE name = 'Kevin Bacon' AND birth = 1958))) AND NOT name = 'Kevin Bacon' AND NOT birth = 1958;